$(document).ready(function () {
     $('.datepicker').datepicker({
          format: "dd/mm/yyyy"
     }).on('changeDate', function (e) {
          $(this).datepicker('hide');
     });

     $('.editor').redactor({
          focus: true,
          lang: 'en',
          imageUpload: 'ajax/upload.php',
          plugins: ['fontfamily', 'fontsize', 'fullscreen', 'fontcolor', 'table', 'imagemanager']
     });
     /*Change password*/

     $("#frmChangePassword").submit(function (e) {
          e.preventDefault();
     }).validate({
          rules: {
               old: "required",
               'new': {
                    required: true,
                    minlength: 8
               },
               new_confirm: {
                    equalTo: '#new'
               }
          },
          messages: {
               old: "Old password required",
               'new': {
                    required: "New Password required",
                    minlength: "Password at least 8 characters in length"
               },
               new_confirm: {
                    equalTo: 'Password missmatch'
               }
          },
          submitHandler: function (form) {
               form.submit();
          }
     });
     /*Form product*/
     $("#frmProduct").submit(function (e) {
          e.preventDefault();
     }).validate({
          rules: {
               'product[prd_name]': "required"
          },
          messages: {
               'product[prd_name]': "Please enter product name"
          },
          submitHandler: function (form) {
               form.submit();
          }
     });
     /*Form product*/
});

function showMessage(message, type) {
     $('.success').hide();
     $('.error').hide();
     $('.info').hide();
     $('.alert').hide();
     if (type == 'success') {
          $('.success').slideDown(500);
          $('.sus_msg').html(message);
     } else if (type == 'fail') {
          $('.error').slideDown(500);
          $('.err_msg').html(message);
     } else if (type == 'info') {
          $('.info').slideDown(500);
          $('.err_inf').html(message);
     } else if (type == 'alert') {
          $('.alert').slideDown(500);
          $('.err_alt').html(message);
     }
}